import React, { useState, useEffect } from 'react';
import { QrCode } from 'lucide-react';
import QRGenerator from './components/QRGenerator';
import QRList from './components/QRList';
import ServerSelector from './components/ServerSelector';
import { healthCheck } from './services/api';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState('generate');
  const [refreshTrigger, setRefreshTrigger] = useState(0);
  const [apiStatus, setApiStatus] = useState('checking');
  const [serverSelectorVisible, setServerSelectorVisible] = useState(false);

  useEffect(() => {
    checkAPIHealth();
  }, []);

  const checkAPIHealth = async () => {
    const health = await healthCheck();
    setApiStatus(health.status === 'OK' ? 'online' : 'offline');
  };

  const handleGenerated = (newQR) => {
    setRefreshTrigger(prev => prev + 1);
    setActiveTab('list');
  };

  return (
    <div className="app">
      <div className="container">
        {/* Header */}
        <header className="header">
  <div className="header-content">
    <div className="logo">
      <QrCode size={40} />
      <div>
        <h1>QR Code Generator & Tracker</h1>
        <p className="subtitle">Create trackable QR codes with MongoDB storage</p>
      </div>
    </div>

    {/* Right side: API status + Server Selector */}
    <div className="header-right">
      <div className="status-indicator">
        <span className={`status-dot ${apiStatus}`}></span>
        <span className="status-text">
          {apiStatus === 'online' ? 'API Connected' :
           apiStatus === 'offline' ? 'API Offline' :
           'Checking...'}
        </span>
      </div>

      <button
        className="btn-open-server-selector"
        onClick={() => setServerSelectorVisible(true)}
      >
        Select Server
      </button>
    </div>
  </div>
</header>


        {/* Server Selector */}
        {serverSelectorVisible && (
          <ServerSelector onClose={() => setServerSelectorVisible(false)} />
        )}

        {/* Tabs */}
        <div className="tabs">
          <button
            className={`tab ${activeTab === 'generate' ? 'active' : ''}`}
            onClick={() => setActiveTab('generate')}
          >
            Generate QR
          </button>
          <button
            className={`tab ${activeTab === 'list' ? 'active' : ''}`}
            onClick={() => setActiveTab('list')}
          >
            My QR Codes
          </button>
        </div>

        {/* Content */}
        <div className="content">
          {apiStatus === 'offline' && (
            <div className="alert alert-error">
              <strong>‚ö†Ô∏è Backend is offline</strong>
              <p>Please make sure the backend server is running on port 5000</p>
              <button onClick={checkAPIHealth} className="btn btn-sm">
                Retry Connection
              </button>
            </div>
          )}

          {activeTab === 'generate' ? (
            <QRGenerator onGenerated={handleGenerated} />
          ) : (
            <QRList refreshTrigger={refreshTrigger} />
          )}
        </div>

        {/* Footer */}
        <footer className="footer">
          <p>üí° Built with React + MongoDB ‚Ä¢ All QR codes stored in database</p>
        </footer>
      </div>
    </div>
  );
}

export default App;
